css:
.flex-col {
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: flex-start;
}

.heading-primary {
    font-family: "Times New Roman";
    font-size: 28px;
    font-weight: bold;
    text-transform: uppercase;
}

.blue {
    color: darkblue;
}

.mar-btm {
    margin-bottom: 5px;
}

.jc-title {
    width: 100%;
    font-size: 18px;
    background-color: #d91a30;
    color: #fff;
    text-align: center;
}

.jc-tab-container {
    display: flex;
    justify-content: flex-start;
    align-items: flex-start;
    width: 100%;
    height: 204px;
}

.cont-info-tab {
    border: 1px solid #000;
    width: 100%;
    height: 100%;
}    

.cont-info-tab-1 {
    margin-right: 5px;
    width: 60%;
}

.print-format td, .print-format th {
    padding: 5px 2px !important;
    font-size: 12px;
    color: #000 !important;
}

.cont-info-tab-2 {
    width: 40%;
}

.cont-info-tab-2 td,
.cont-info-tab-2 th{
    width: 40%;
    padding: 2px !important;
}

.items-table,
.items-table td,
.items-table th{
    border: 1px solid #000;
    font-family: "Times New Roman";
}

.sign-table {
    border: 1px solid #000;
}

.items-table th {
    text-align: center;
}

.amts-table {
    border: 1px solid #000;
}

.center {
    text-align: center;
}

.br-r {
    border-right: 1px solid #000;
}

.currency {
    text-align: right;
}

.w-68 {
    width: 68%;
}

.bold {
    font-weight: 600;
}

table {
    width: 100% !important;
}
